class SampleClass {
  member: {};

  method() {
    this.member = {};
    const shorthand = 1;
    const isValid: boolean = null;

    // bad
    if (isValid === true) {
      // ...
    }

    // good
    if (isValid) {
      // ...
    }

    new Promise((resolve, reject) => {
    });

    return {
      a: 1,
      shorthand,
      b: 1
    };
  }
}

function main() {
  const sample = new SampleClass();
  sample.method();
}

main();

function rule15_7_bad(a: any, b: any, c: any) {
  const foo = a ? a : b;
  const bar = c ? true : false;
  const baz = c ? false : true;
}

function rule15_7_good(a: any, b: any, c: any) {
  const foo = a || b;
  const bar = !!c;
  const baz = !c;

  return { foo, bar, baz };
}
